const Header = (props) => {
  console.log(props.course)
  return (
      <h1>{props.course.name}</h1>
  )
}

const Content = (props) => {
  return (
      <div>
        {props.parts.map(part => 
        <p key={part.id}>
          {part.name} {part.exercises}
          </p>)}
      </div>
  )
}

